package common

import (
	"testing"
)

func TestEncoding(t *testing.T) {
	hash := []byte("")
	text := []byte("Правительство как грудной младенец: чудовищный аппетит на одном конце и полная безответственность на другом. Here is also some umlauts: üöäß")
	r1 := IsHexData(hash)
	r2 := IsAscii(hash)
	r3 := IsAscii(text)
	if !r1 || !r2 || !r3 {
		t.Fatal("false negative")
	}
}
